import React from 'react';
import DynamicText from '../dynamictext';
import { render, cleanup } from '@testing-library/react';
import '@testing-library/jest-dom';

afterEach(cleanup);

it('renders correctly', async () => {
    const { getByTestId } = render(
        <DynamicText testId='testDynamicText' className='testClassName'>{'test dynamic text'}</DynamicText>
    );
    const container = getByTestId('testDynamicText');
    const text = getByTestId('dynamicTextLabel');

    expect(container).toHaveClass('dynamictext');
    expect(text).toHaveClass('dynamictext-label');

    expect(text.textContent).toBe('test dynamic text');
});
