import React, { useCallback, useState } from 'react';
import PropTypes from 'prop-types';
import cn from 'classnames';
import Menu from '../../assets/images/menu.png';
import Logo from '../../assets/images/metlife-logo.png';
import './header.scss';

const Header = ({testID, className, onExpand, onHeaderClick }) => {
    const [isMenuCollapsed, setIsMenuCollapsed] = useState(false);

    const expandMenu = useCallback(() => {
        setIsMenuCollapsed();
        if(onExpand){
            onExpand();
        }
    }, [setIsMenuCollapsed]);

    const onHeaderIconClick = () => {
        if(onHeaderClick){
            onHeaderClick();
        }
    };

    return (
        <div
            data-testid={testID}
            className={cn(
                'header',
                {
                    'header--collapsed': isMenuCollapsed,
                },
                className,
            )}
        >
            <img data-testid={'hamburger'} className='header__menu' src={Menu} onClick={expandMenu} />
            <img
            data-testid={'logo'}
            src={Logo}
            className={'header__logo'}
            onClick={onHeaderIconClick}
             />
    
        </div>
    );
};

Header.propTypes = {
    className: PropTypes.string,
    onExpand: PropTypes.func,
    onHeaderClick: PropTypes.func
};

export default Header;
