import React, { forwardRef } from 'react';
import cn from 'classnames';
import PropTypes from 'prop-types';
import './icon.scss';

const Icon = forwardRef(
    ({ testId, name, src, className, onClick, isActive, ...props }, ref) => {

        const onIconClick = () => {
            if(isActive){
                if(onClick) onClick();
            }
        }

        return (
            <img
                ref={ref}
                data-testid={testId ?? 'icon-img'}
                onClick={onIconClick}
                className={cn('met-icon', className, {'met-icon-disabled': !isActive})}
                src={src}
                alt={name}
                {...props}
            />
        );
    },
);

Icon.propTypes = {
    testId: PropTypes.string,
    name: PropTypes.string.isRequired,
    className: PropTypes.string,
    onClick: PropTypes.func,
    src:PropTypes.string,
    isActive: PropTypes.bool
};

Icon.defaultProps = {
    isActive : true
}

export default Icon;
