import React from "react";
import Loader from "../loader";
import { render, cleanup } from "@testing-library/react";
import "@testing-library/jest-dom";
import { expect, it, afterEach } from "@jest/globals";

afterEach(cleanup);

it("renders correctly", async () => {
  const { getByTestId } = render(
    <Loader className="testClassName" message="test"></Loader>
  );
  const container = getByTestId("loader-container");
  const message = getByTestId("loader-message");

  expect(container).toHaveClass("met-loader", "testClassName");
  expect(message).toHaveClass("met-loader__message");
  expect(message.textContent).toBe("test");
});
