import React from 'react';
import PropTypes from 'prop-types';
import cn from 'classnames';
import './loader.scss';

const Loader = ({ isLoading, className, testId, message, loaderMessageClass, loaderSpinnerClass }) => {
    if (!isLoading) return null;

    return (
        <div data-testid={testId ?? 'loader-container'} className={cn('met-loader', className)}>
            {[...new Array(3)].map((num, idx) => (
                <div key={idx} className={cn('met-loader__spinner', loaderSpinnerClass)}></div>
            ))}
            {message && (
                <div data-testid='loader-message' className={cn('met-loader__message', loaderMessageClass)}>{message}</div>
            )}
        </div>
    );
};

Loader.propTypes = {
    isLoading: PropTypes.bool.isRequired,
    className: PropTypes.string,
    loaderMessageClass: PropTypes.string,
    loaderSpinnerClass: PropTypes.string,
    message: PropTypes.oneOfType([PropTypes.string, PropTypes.node]),
};

Loader.defaultProps = {
    isLoading: true,
};

export default Loader;
