import React from 'react';
import PropTypes from 'prop-types';
import cn from 'classnames';
import Back from '../../assets/images/back.png';
import './subheader.scss';

const SubHeader = ({ testID, className, title, navigate }) => {
    const IconClick = () => {
        if(navigate){
            navigate();
        }
    }
    return (
        <div id={testID} data-testid={testID ?? 'subheader'} className={cn('subheader', className)}>
            <img
                data-testid={'backIcon'}
                className='subheader__icon'
                src={Back}
                onClick={IconClick}
            />
            <div className='subheader__title'>{title}</div>
        </div>
    );
};

SubHeader.propTypes = {
    testID: PropTypes.string,
    className: PropTypes.string,
    title: PropTypes.string,
    navigate: PropTypes.func,
};

export default SubHeader;
